import fetch from "isomorphic-unfetch";
import isEmpty from "lodash/isEmpty";

const Scores = props => {
  return (
    <>
      <h1>Scores</h1>
      <ul>
        {props.matches.map(match => {
          console.log(props.matches);
          console.log(isEmpty(match.MatchResults));
          return (
            <li>
              <div className="score-line">
                <span className="home-team">{match.Team1.TeamName}</span>
                <img src={match.Team1.TeamIconUrl} />
                <span>
                  {!isEmpty(match.MatchResults) &&
                    match.MatchResults[0].PointsTeam1}
                  :
                  {isEmpty(match.MatchResults) &&
                    match.MatchResults[0].PointsTeam2}
                </span>
                <img src={match.Team2.TeamIconUrl} />
                <span>{match.Team2.TeamName}</span>
              </div>
            </li>
          );
        })}
      </ul>
      <style jsx>
        {`
          ul {
            padding: 0;
          }

          li {
            list-style: none;
          }

          .score-line {
            display: flex;
            margin: 5px 0;
            display: grid;
            grid-template-columns: 0.2fr 42px 30px 0.01fr 0.2fr;
          }

          li:nth-of-type(odd) {
            background-color: #eee;
          }

          img {
            margin: 0 10px;
            max-width: 20px;
          }
          .home-team {
            display: flex;
            flex-direction: row-reverse;
          }
        `}
      </style>
    </>
  );
};

export default Scores;

Scores.getInitialProps = async function() {
  const res = await fetch("https://www.openligadb.de/api/getmatchdata/bl1");
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);
  console.log(data);
  return {
    matches: data
  };
};
